pragma solidity ^0.5.0;

contract LightValidation {
  struct Witness {
    uint256 deposit;
    uint256 weight;
  }
  mapping (address => Witness) public Witnesses;

  struct Device {
    uint id;
    bool isActive;
  }
  address[] DeviceAddress;
  mapping (address => Device) public Devices;
  event DeviceRegistered(address indexed device);

  uint public numWitness;
  uint public numDevice;

  constructor() public {
    // empty for now
    numWitness = 0;
    numDevice = 0;
  }

  function deviceExist(address device) public view returns (bool isExist) {
    if (DeviceAddress.length == 0)
      return false;
    return (DeviceAddress[Devices[device].id] == device);
  }

  function getDeviceList() public view returns (address[] memory ) {
    return DeviceAddress;
  }

  function registerDevice() public {
    require(!deviceExist(msg.sender), "Device already registered.");
    Devices[msg.sender].isActive = true;
    DeviceAddress.push(msg.sender);
    numDevice++;
    emit DeviceRegistered(msg.sender);
  }

  function retireDevice() public {
    Devices[msg.sender].isActive = false;
    delete DeviceAddress[Devices[msg.sender].id];
    numDevice--;
  }

  function registerWitness() public payable {
    Witnesses[msg.sender].deposit += msg.value;
    Witnesses[msg.sender].weight += msg.value;
    numWitness++;
  }

  function retireWitness() public {
    msg.sender.transfer(Witnesses[msg.sender].deposit);
    Witnesses[msg.sender].deposit = 0;
    numWitness--;
  }
  
}
